import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const foodstore = createSlice({
  name: "foods",
  initialState: {
    foodsList: [],
  },

  reducers: {
    setFoodsList(state, action) {
      state.foodsList = action.payload;
    },
  },
});

// asych
const fetchFoodsList = () => {
  return async (dispatch) => {
    axios.get("");
  };
};
