import { createSlice } from "@reduxjs/toolkit";

const foodstore = createSlice({
  name: "foods",
  initialState: {
    foodsList: [],
  },

  reducers: {
    setFoodsList(state, action) {
      state.foodsList = action.payload;
    },
  },
});
